import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertPinSchema, insertExpenseSchema, insertJournalSchema, insertPersonSchema, insertRoutineItemSchema, insertPackingItemSchema, insertMealLogSchema, insertWaterLogSchema } from "@shared/schema";
import { parseCommand, parseQuery, generateResponse } from "./openai";
import multer from "multer";
import path from "path";
import fs from "fs";

export async function registerRoutes(app: Express): Promise<Server> {
  // Ensure uploads directory exists
  const uploadsDir = path.join(process.cwd(), 'uploads');
  if (!fs.existsSync(uploadsDir)) {
    fs.mkdirSync(uploadsDir, { recursive: true });
  }

  // Configure multer for file uploads
  const storage_config = multer.diskStorage({
    destination: (req, file, cb) => {
      cb(null, uploadsDir);
    },
    filename: (req, file, cb) => {
      const uniqueSuffix = Date.now() + '-' + Math.round(Math.random() * 1E9);
      cb(null, file.fieldname + '-' + uniqueSuffix + path.extname(file.originalname));
    }
  });

  const upload = multer({ 
    storage: storage_config,
    limits: { fileSize: 5 * 1024 * 1024 }, // 5MB limit
    fileFilter: (req, file, cb) => {
      if (file.mimetype.startsWith('image/')) {
        cb(null, true);
      } else {
        cb(new Error('Only image files are allowed'));
      }
    }
  });

  // Serve uploaded files
  app.use('/uploads', (req, res, next) => {
    // Add basic security headers
    res.setHeader('Cache-Control', 'public, max-age=31536000');
    next();
  }, (req, res, next) => {
    const filePath = path.join(uploadsDir, req.path);
    if (fs.existsSync(filePath)) {
      res.sendFile(filePath);
    } else {
      res.status(404).json({ message: 'File not found' });
    }
  });

  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Natural language command processing
  app.post('/api/command', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { command } = req.body;

      // Use OpenAI to parse and execute commands
      const result = await parseAndExecuteCommand(command, userId);
      res.json(result);
    } catch (error) {
      console.error("Error processing command:", error);
      res.status(500).json({ message: "Failed to process command" });
    }
  });

  // AI-powered query endpoint
  app.post('/api/query', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { query } = req.body;

      // Parse query with OpenAI
      const intent = await parseQuery(query, ['people', 'expenses', 'journal', 'pins']);

      let searchResults = [];

      // Execute the query based on parsed intent
      if (intent.entity === 'people') {
        searchResults = await executePersonQuery(userId, intent.filters);
      } else if (intent.entity === 'expenses') {
        searchResults = await executeExpenseQuery(userId, intent.filters);
      } else if (intent.entity === 'journal') {
        searchResults = await executeJournalQuery(userId, intent.filters);
      } else if (intent.entity === 'pins') {
        searchResults = await executePinQuery(userId, intent.filters);
      }

      // Generate natural language response
      const response = await generateResponse(query, searchResults);

      res.json({
        success: true,
        response,
        data: searchResults,
        intent,
      });
    } catch (error) {
      console.error("Error processing query:", error);
      res.status(500).json({ message: "Failed to process query" });
    }
  });

  // Pins routes
  app.get('/api/pins', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const pins = await storage.getPins(userId);
      res.json(pins);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch pins" });
    }
  });

  app.post('/api/pins', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const pinData = insertPinSchema.parse({ ...req.body, userId });
      const pin = await storage.createPin(pinData);
      res.json(pin);
    } catch (error) {
      res.status(400).json({ message: "Invalid pin data" });
    }
  });

  // Expenses routes
  app.get('/api/expenses', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const date = req.query.date as string;
      const expenses = await storage.getExpenses(userId, date);
      res.json(expenses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch expenses" });
    }
  });

  app.post('/api/expenses', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const expenseData = insertExpenseSchema.parse({ ...req.body, userId });
      const expense = await storage.createExpense(expenseData);
      res.json(expense);
    } catch (error) {
      res.status(400).json({ message: "Invalid expense data" });
    }
  });

  // Journal routes
  app.get('/api/journal', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const entries = await storage.getJournalEntries(userId);
      res.json(entries);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch journal entries" });
    }
  });

  app.post('/api/journal', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const entryData = insertJournalSchema.parse({ ...req.body, userId });
      const entry = await storage.createJournalEntry(entryData);
      res.json(entry);
    } catch (error) {
      res.status(400).json({ message: "Invalid journal entry data" });
    }
  });

  // People routes
  app.get('/api/people', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const query = req.query.search as string;

      let people;
      if (query) {
        people = await storage.searchPeople(userId, query);
      } else {
        people = await storage.getPeople(userId);
      }

      res.json(people);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch people" });
    }
  });

  app.post('/api/people', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const personData = insertPersonSchema.parse({ ...req.body, userId });
      const person = await storage.createPerson(personData);
      res.json(person);
    } catch (error) {
      res.status(400).json({ message: "Invalid person data" });
    }
  });

  // Upload image for a person
  app.post('/api/people/:id/upload', isAuthenticated, upload.single('photo'), async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const personId = req.params.id;

      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      const photoUrl = `/uploads/${req.file.filename}`;

      // Update the person with the photo URL
      const updatedPerson = await storage.updatePersonPhoto(personId, userId, photoUrl);

      res.json({ 
        success: true, 
        photoUrl,
        person: updatedPerson 
      });
    } catch (error) {
      console.error("Error uploading person photo:", error);
      res.status(500).json({ message: "Failed to upload photo" });
    }
  });

  // General file upload endpoint
  app.post('/api/upload', isAuthenticated, upload.single('file'), async (req: any, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      const fileUrl = `/uploads/${req.file.filename}`;

      res.json({ 
        success: true, 
        url: fileUrl,
        filename: req.file.filename,
        originalName: req.file.originalname,
        size: req.file.size
      });
    } catch (error) {
      console.error("Error uploading file:", error);
      res.status(500).json({ message: "Failed to upload file" });
    }
  });

  // Routine routes
  app.get('/api/routine', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const items = await storage.getRoutineItems(userId);
      res.json(items);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch routine items" });
    }
  });

  app.post('/api/routine', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const itemData = insertRoutineItemSchema.parse({ ...req.body, userId });
      const item = await storage.createRoutineItem(itemData);
      res.json(item);
    } catch (error) {
      res.status(400).json({ message: "Invalid routine item data" });
    }
  });

  app.post('/api/routine/:id/check', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const routineId = req.params.id;
      const date = req.body.date || new Date().toISOString().split('T')[0];
      await storage.markRoutineDone(userId, routineId, date);
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ message: "Failed to mark routine done" });
    }
  });

  // Packing routes
  app.get('/api/packing', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const region = req.query.region as string;
      const items = await storage.getPackingItems(userId, region);
      res.json(items);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch packing items" });
    }
  });

  app.post('/api/packing', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const itemData = insertPackingItemSchema.parse({ ...req.body, userId });
      const item = await storage.createPackingItem(itemData);
      res.json(item);
    } catch (error) {
      res.status(400).json({ message: "Invalid packing item data" });
    }
  });

  app.patch('/api/packing/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const itemId = req.params.id;
      const { packed } = req.body;
      const item = await storage.togglePackingItem(itemId, userId, packed);
      res.json(item);
    } catch (error) {
      res.status(500).json({ message: "Failed to update packing item" });
    }
  });

  // Food & Water routes
  app.post('/api/meals', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const mealData = insertMealLogSchema.parse({ ...req.body, userId });
      const meal = await storage.createMealLog(mealData);
      res.json(meal);
    } catch (error) {
      res.status(400).json({ message: "Invalid meal data" });
    }
  });

  app.post('/api/water', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const waterData = insertWaterLogSchema.parse({ ...req.body, userId });
      const water = await storage.createWaterLog(waterData);
      res.json(water);
    } catch (error) {
      res.status(400).json({ message: "Invalid water data" });
    }
  });

  // Dashboard stats
  app.get('/api/stats/today', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const stats = await storage.getTodayStats(userId);
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch today's stats" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

// Query execution functions
async function executePersonQuery(userId: string, filters: Record<string, any>) {
  let people = await storage.getPeople(userId);

  if (filters.whereMet) {
    people = people.filter(p => 
      p.whereMet?.toLowerCase().includes(filters.whereMet.toLowerCase())
    );
  }

  if (filters.name) {
    people = people.filter(p => 
      p.name.toLowerCase().includes(filters.name.toLowerCase())
    );
  }

  return people;
}

async function executeExpenseQuery(userId: string, filters: Record<string, any>) {
  const date = filters.date === 'today' ? new Date().toISOString().split('T')[0] : filters.date;
  let expenses = await storage.getExpenses(userId, date);

  if (filters.category) {
    expenses = expenses.filter(e => e.category === filters.category);
  }

  if (filters.minAmount) {
    expenses = expenses.filter(e => parseFloat(e.amount) >= filters.minAmount);
  }

  return expenses;
}

async function executeJournalQuery(userId: string, filters: Record<string, any>) {
  let entries = await storage.getJournalEntries(userId);

  if (filters.keyword) {
    entries = entries.filter(e => 
      e.title?.toLowerCase().includes(filters.keyword.toLowerCase()) ||
      e.body?.toLowerCase().includes(filters.keyword.toLowerCase())
    );
  }

  return entries;
}

async function executePinQuery(userId: string, filters: Record<string, any>) {
  let pins = await storage.getPins(userId);

  if (filters.status) {
    pins = pins.filter(p => p.status === filters.status);
  }

  if (filters.address) {
    pins = pins.filter(p => 
      p.address?.toLowerCase().includes(filters.address.toLowerCase()) ||
      p.title?.toLowerCase().includes(filters.address.toLowerCase())
    );
  }

  return pins;
}

// OpenAI-enhanced command parser
async function parseAndExecuteCommand(command: string, userId: string) {
  try {
    // First, try OpenAI parsing
    const intent = await parseCommand(command);

    if (intent.confidence > 0.7) {
      // Execute the parsed command
      if (intent.action === 'add_person') {
        const person = await storage.createPerson({
          userId,
          name: intent.data.name,
          phone: intent.data.phone || null,
          whatsapp: intent.data.whatsapp || null,
          email: intent.data.email || null,
          whereMet: intent.data.whereMet || null,
          notes: intent.data.notes || null,
        });
        return { success: true, message: `Added ${person.name} to contacts`, data: person };
      }

      if (intent.action === 'add_expense') {
        const expense = await storage.createExpense({
          userId,
          amount: intent.data.amount,
          category: intent.data.category || "misc",
          note: intent.data.note || command,
        });
        return { success: true, message: `Logged ₹${expense.amount} expense`, data: expense };
      }

      if (intent.action === 'add_water') {
        const water = await storage.createWaterLog({
          userId,
          quantityMl: intent.data.quantityMl || 250,
        });
        return { success: true, message: `Logged ${water.quantityMl}ml water intake`, data: water };
      }

      if (intent.action === 'add_meal') {
        const mealLog = await storage.createMealLog({
          userId,
          meal: intent.data.meal,
          note: intent.data.note || command,
        });
        return { success: true, message: `Logged ${mealLog.meal}`, data: mealLog };
      }

      if (intent.action === 'add_journal') {
        const entry = await storage.createJournalEntry({
          userId,
          title: intent.data.title || "Quick Entry",
          body: intent.data.body || command,
        });
        return { success: true, message: "Added journal entry", data: entry };
      }

      if (intent.action === 'add_pin') {
        const pin = await storage.createPin({
          userId,
          title: intent.data.title,
          address: intent.data.address || null,
          notes: intent.data.notes || null,
          status: intent.data.status || "planned",
        });
        return { success: true, message: `Added ${pin.title} to travel plans`, data: pin };
      }
    }

    // Fallback to regex-based parsing for backward compatibility
    return await fallbackCommandParsing(command, userId);

  } catch (error) {
    console.error("Error in AI command parsing:", error);
    // Fallback to regex-based parsing
    return await fallbackCommandParsing(command, userId);
  }
}

// Fallback command parser using regex patterns
async function fallbackCommandParsing(command: string, userId: string) {
  const cmd = command.toLowerCase().trim();

  // Add person commands
  if (cmd.includes("add person") || cmd.includes("add contact")) {
    const nameMatch = cmd.match(/(?:add person|add contact)\s+(.+?)(?:,|\s+(?:phone|number|contact))/i);
    const phoneMatch = cmd.match(/(?:phone|number|contact)\s+([+\d\s-]+)/i);
    const locationMatch = cmd.match(/(?:at|in|met at)\s+([^,]+)/i);

    if (nameMatch) {
      const person = await storage.createPerson({
        userId,
        name: nameMatch[1].trim(),
        phone: phoneMatch ? phoneMatch[1].trim() : null,
        whereMet: locationMatch ? locationMatch[1].trim() : null,
      });
      return { success: true, message: `Added ${person.name} to contacts`, data: person };
    }
  }

  // Add expense commands
  if (cmd.includes("expense") || cmd.includes("spent") || cmd.includes("cost")) {
    const amountMatch = cmd.match(/[₹$]?(\d+(?:\.\d{2})?)/);
    const categoryMatch = cmd.match(/(?:for|on)\s+(food|transport|stay|gear|misc)/i);

    if (amountMatch) {
      const expense = await storage.createExpense({
        userId,
        amount: amountMatch[1],
        category: categoryMatch ? categoryMatch[1].toLowerCase() as any : "misc",
        note: command,
      });
      return { success: true, message: `Logged ₹${expense.amount} expense`, data: expense };
    }
  }

  // Add water commands
  if (cmd.includes("water") && (cmd.includes("ml") || cmd.includes("glass"))) {
    const amountMatch = cmd.match(/(\d+)\s*(?:ml|glass)/);
    const quantity = amountMatch ? parseInt(amountMatch[1]) : 250;

    const water = await storage.createWaterLog({
      userId,
      quantityMl: quantity,
    });
    return { success: true, message: `Logged ${quantity}ml water intake`, data: water };
  }

  // Add meal commands
  if (cmd.includes("breakfast") || cmd.includes("lunch") || cmd.includes("dinner") || cmd.includes("snack")) {
    let meal = "snack";
    if (cmd.includes("breakfast")) meal = "breakfast";
    else if (cmd.includes("lunch")) meal = "lunch";
    else if (cmd.includes("dinner")) meal = "dinner";

    const mealLog = await storage.createMealLog({
      userId,
      meal: meal as any,
      note: command,
    });
    return { success: true, message: `Logged ${meal}`, data: mealLog };
  }

  // Add journal commands
  if (cmd.includes("journal") || cmd.includes("note") || cmd.includes("today")) {
    const entry = await storage.createJournalEntry({
      userId,
      title: "Quick Entry",
      body: command,
    });
    return { success: true, message: "Added journal entry", data: entry };
  }

  return { success: false, message: "Command not recognized. Try: 'add person [name], phone [number]' or 'expense 100 for food'" };
}

server/storage.ts.content:
import {
  users,
  pins,
  expenses,
  journal,
  people,
  routineItems,
  routineChecks,
  packingItems,
  packingCategories,
  mealLogs,
  waterLogs,
  transportLogs,
  stayLogs,
  emergencyContacts,
  moodLogs,
  wishlist,
  barterLogs,
  type User,
  type UpsertUser,
  type Pin,
  type InsertPin,
  type Expense,
  type InsertExpense,
  type Journal,
  type InsertJournal,
  type Person,
  type InsertPerson,
  type RoutineItem,
  type InsertRoutineItem,
  type PackingItem,
  type InsertPackingItem,
  type MealLog,
  type InsertMealLog,
  type WaterLog,
  type InsertWaterLog,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Travel operations
  getPins(userId: string): Promise<Pin[]>;
  createPin(pin: InsertPin): Promise<Pin>;
  updatePin(id: string, userId: string, updates: Partial<InsertPin>): Promise<Pin>;

  getExpenses(userId: string, date?: string): Promise<Expense[]>;
  createExpense(expense: InsertExpense): Promise<Expense>;

  getJournalEntries(userId: string): Promise<Journal[]>;
  createJournalEntry(entry: InsertJournal): Promise<Journal>;

  getPeople(userId: string): Promise<Person[]>;
  createPerson(person: InsertPerson): Promise<Person>;
  updatePersonPhoto(id: string, userId: string, photoUrl: string): Promise<Person>;
  searchPeople(userId: string, query: string): Promise<Person[]>;

  getRoutineItems(userId: string): Promise<RoutineItem[]>;
  createRoutineItem(item: InsertRoutineItem): Promise<RoutineItem>;
  markRoutineDone(userId: string, routineId: string, date: string): Promise<void>;
  getRoutineChecks(userId: string, date: string): Promise<any[]>;

  getPackingItems(userId: string, region?: string): Promise<PackingItem[]>;
  createPackingItem(item: InsertPackingItem): Promise<PackingItem>;
  togglePackingItem(id: string, userId: string, packed: boolean): Promise<PackingItem>;

  getMealLogs(userId: string, date: string): Promise<MealLog[]>;
  createMealLog(log: InsertMealLog): Promise<MealLog>;

  getWaterLogs(userId: string, date: string): Promise<WaterLog[]>;
  createWaterLog(log: InsertWaterLog): Promise<WaterLog>;

  getTodayStats(userId: string): Promise<{
    routineCompletion: string;
    waterGlasses: number;
    mealsCompleted: string[];
    totalExpenses: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Travel operations
  async getPins(userId: string): Promise<Pin[]> {
    return await db.select().from(pins).where(eq(pins.userId, userId)).orderBy(desc(pins.createdAt));
  }

  async createPin(pin: InsertPin): Promise<Pin> {
    const [newPin] = await db.insert(pins).values(pin).returning();
    return newPin;
  }

  async updatePin(id: string, userId: string, updates: Partial<InsertPin>): Promise<Pin> {
    const [updatedPin] = await db
      .update(pins)
      .set(updates)
      .where(and(eq(pins.id, id), eq(pins.userId, userId)))
      .returning();
    return updatedPin;
  }

  async getExpenses(userId: string, date?: string): Promise<Expense[]> {
    const query = db.select().from(expenses).where(eq(expenses.userId, userId));
    if (date) {
      query.where(and(eq(expenses.userId, userId), eq(expenses.spentAt, date)));
    }
    return await query.orderBy(desc(expenses.spentAt));
  }

  async createExpense(expense: InsertExpense): Promise<Expense> {
    const [newExpense] = await db.insert(expenses).values(expense).returning();
    return newExpense;
  }

  async getJournalEntries(userId: string): Promise<Journal[]> {
    return await db.select().from(journal).where(eq(journal.userId, userId)).orderBy(desc(journal.taggedAt));
  }

  async createJournalEntry(entry: InsertJournal): Promise<Journal> {
    const [newEntry] = await db.insert(journal).values(entry).returning();
    return newEntry;
  }

  async getPeople(userId: string): Promise<Person[]> {
    return await db.select().from(people).where(eq(people.userId, userId)).orderBy(desc(people.metOn));
  }

  async createPerson(person: InsertPerson): Promise<Person> {
    const [newPerson] = await db.insert(people).values(person).returning();
    return newPerson;
  }

  async searchPeople(userId: string, query: string): Promise<Person[]> {
    return await db.select().from(people)
      .where(and(
        eq(people.userId, userId),
        sql`(${people.name} ILIKE ${`%${query}%`} OR ${people.whereMet} ILIKE ${`%${query}%`})`
      ))
      .orderBy(desc(people.metOn));
  }

  async getRoutineItems(userId: string): Promise<RoutineItem[]> {
    return await db.select().from(routineItems).where(eq(routineItems.userId, userId));
  }

  async createRoutineItem(item: InsertRoutineItem): Promise<RoutineItem> {
    const [newItem] = await db.insert(routineItems).values(item).returning();
    return newItem;
  }

  async markRoutineDone(userId: string, routineId: string, date: string): Promise<void> {
    await db.insert(routineChecks).values({
      userId,
      routineId,
      doneOn: date,
      done: true,
    });
  }

  async getRoutineChecks(userId: string, date: string): Promise<any[]> {
    return await db.select().from(routineChecks)
      .where(and(eq(routineChecks.userId, userId), eq(routineChecks.doneOn, date)));
  }

  async getPackingItems(userId: string, region?: string): Promise<PackingItem[]> {
    const query = db.select().from(packingItems).where(eq(packingItems.userId, userId));
    if (region && region !== "All") {
      query.where(and(eq(packingItems.userId, userId), eq(packingItems.region, region)));
    }
    return await query;
  }

  async createPackingItem(item: InsertPackingItem): Promise<PackingItem> {
    const [newItem] = await db.insert(packingItems).values(item).returning();
    return newItem;
  }

  async togglePackingItem(id: string, userId: string, packed: boolean): Promise<PackingItem> {
    const [updatedItem] = await db
      .update(packingItems)
      .set({ packed })
      .where(and(eq(packingItems.id, id), eq(packingItems.userId, userId)))
      .returning();
    return updatedItem;
  }

  async getMealLogs(userId: string, date: string): Promise<MealLog[]> {
    return await db.select().from(mealLogs)
      .where(and(eq(mealLogs.userId, userId), eq(mealLogs.eatenAt, date)));
  }

  async createMealLog(log: InsertMealLog): Promise<MealLog> {
    const [newLog] = await db.insert(mealLogs).values(log).returning();
    return newLog;
  }

  async getWaterLogs(userId: string, date: string): Promise<WaterLog[]> {
    return await db.select().from(waterLogs)
      .where(and(eq(waterLogs.userId, userId), sql`DATE(${waterLogs.loggedAt}) = ${date}`));
  }

  async createWaterLog(log: InsertWaterLog): Promise<WaterLog> {
    const [newLog] = await db.insert(waterLogs).values(log).returning();
    return newLog;
  }

  async getTodayStats(userId: string): Promise<{
    routineCompletion: string;
    waterGlasses: number;
    mealsCompleted: string[];
    totalExpenses: number;
  }> {
    const today = new Date().toISOString().split('T')[0];

    // Get routine completion
    const routineItems = await this.getRoutineItems(userId);
    const routineChecks = await this.getRoutineChecks(userId, today);
    const routineCompletion = `${routineChecks.length}/${routineItems.length}`;

    // Get water intake
    const waterLogs = await this.getWaterLogs(userId, today);
    const waterGlasses = Math.floor(waterLogs.reduce((sum, log) => sum + (log.quantityMl || 0), 0) / 250);

    // Get meals completed
    const mealLogs = await this.getMealLogs(userId, today);
    const mealsCompleted = mealLogs.map(log => log.meal);

    // Get total expenses
    const todayExpenses = await this.getExpenses(userId, today);
    const totalExpenses = todayExpenses.reduce((sum, exp) => sum + Number(exp.amount), 0);

    return {
      routineCompletion,
      waterGlasses,
      mealsCompleted,
      totalExpenses,
    };
  }
}

export const storage = new DatabaseStorage();

